import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-solicitacao',
  templateUrl: './solicitacao.component.html',
  styleUrls: ['./solicitacao.component.scss']
})
export class SolicitacaoComponent {
  @Input() numeroOS?: string ;
  @Input() solicitante?: string;
}
<div class="solic">
    <h4>Ordem de serviço</h4>
    <h4 class="number">{{ numeroOS }}</h4>
    <button class="ver">Ver ordem</button>
    <p>Solicitante: {{ solicitante }}</p>
    <ng-content></ng-content>
    <hr>
    <button class="aprovar">Aprovar</button>
    <button class="recusar">Recusar</button>
</div>
