import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-botao-filtro',
  templateUrl: './botao-filtro.component.html',
  styleUrls: ['./botao-filtro.component.scss']
})
export class BotaoFiltroComponent {

  @Input() selecionado: boolean = false;
  @Input() descricao: string = '';
  @Output() clicked = new EventEmitter<void>();

  onClick() {
    this.clicked.emit();
  }

}
<button type="button" class="bt1" [ngClass]="{ 'selecionado': selecionado }" (click)="onClick()">
    <!--<i class="fa-solid fa-list-check" style="color: #fff;"></i>-->
    <ng-content></ng-content>
    <span>{{ descricao }}</span>
</button>
