import { Injectable } from '@angular/core';

// Definindo o tipo para as credenciais dos usuários
interface Credenciais {
  [key: string]: { usuario: string; senha: string };
}

@Injectable({
  providedIn: 'root'
})
export class AuthLoginService {
  private usuarioLogado: string | null = null;

  constructor() { }

  fazerLogin(usuario: string, senha: string): boolean {
    // Lógica de autenticação aqui (pode ser uma chamada HTTP para um servidor)
    // Exemplo simplificado
    const credenciais = {
      funcionario: { usuario: 'funcionario', senha: '123' },
      coordenador: { usuario: 'coordenador', senha: '456' },
      cpd: { usuario: 'cpd', senha: '789' }
    };

    const usuarioValido = credenciais[usuario];
    if (usuarioValido && usuarioValido.senha === senha) {
      this.usuarioLogado = usuario;
      localStorage.setItem('usuarioLogado', usuario);
      return true;
    }
    return false;
  }

  getUsuarioLogado(): string | null {
    return this.usuarioLogado;
  }

  logout(): void {
    console.log(this.usuarioLogado);
    this.usuarioLogado = null;
    localStorage.removeItem('usuarioLogado');
  }
}
