import { Component, Input } from '@angular/core';
import { SwitchService } from 'src/app/services/switch.service';


@Component({
  selector: 'app-modal-os',
  templateUrl: './modal-os.component.html',
  styleUrls: ['./modal-os.component.scss']
})
export class ModalOsComponent {
  constructor(private modalSS: SwitchService) { }

  closeModal2(){
    this.modalSS.$modal.emit(false);
  }

  @Input() ordemDeServico: any = { 
    id: 'Ordem de serviço #OS4568SA', 
    local: 'Laboratório de informática 07', 
    data: '14-09-2023', 
    tipo: 'Computador', 
    status: 'Em andamento',
    posicao: 'Mesa 04', 
    serial: '-', 
    descricao: 'O computador não está ligando e ouve bips'
  };

}
<div class="modal">
    <div class="modal-body">
      <button class="fechar" (click)="closeModal2()">X</button> <!--para o de ordens enviadar encaminhar o botão para ver ordens-->
      <h3>{{ ordemDeServico.id }}</h3>
      <p class="bold">Local de ocorrência:</p>
      <p>{{ ordemDeServico.local }}</p>
      <p class="bold">Data da atribuição:</p>
      <p>{{ ordemDeServico.data }}</p>
      <p class="bold">Tipo do equipamento:</p>
      <p>{{ ordemDeServico.tipo }}</p>
      <p class="bold">Status:</p>
      <p>{{ ordemDeServico.status }}</p>
      <p class="bold">Posição do equipamento:</p>
      <p>{{ ordemDeServico.posicao }}</p>
      <p class="bold">Serial do equipamento:</p>
      <p>{{ ordemDeServico.serial }}</p>
      <p class="bold">Descrição do problema:</p>
      <p>{{ ordemDeServico.descricao }}</p>
    </div>
  </div>
