import { Component, Input } from '@angular/core';
import { SwitchService } from 'src/app/services/switch.service';


@Component({
  selector: 'app-modal-os',
  templateUrl: './modal-os.component.html',
  styleUrls: ['./modal-os.component.scss']
})
export class ModalOsComponent {
  constructor(private modalSS: SwitchService) { }

  closeModal(){
    this.modalSS.$modal.emit(false);
  }

  @Input() ordemDeServico: any[] = [
    { id: 'OS4568SA', status: 'Em andamento', data: '14-09-2023', bt: 'ver' },
    { id: 'OS4567SA', status: 'Concluída', data: '11-07-2023', bt: 'ver' },
    { id: 'OS4566SA', status: 'Rejeitado', data: '27-06-2023', bt: 'ver' }
  ];

}
<div class="modal">
    <div class="modal-body">
      <button class="fechar" (click)="closeModal()">X</button> <!--para o de ordens enviadar encaminhar o botão para ver ordens-->
      <h3>Ordem de serviço #{{ ordemDeServico.numero }}</h3>
      <p>Local de ocorrência:</p>
      <p>{{ ordemDeServico.local }}</p>
      <p>Data da atribuição:</p>
      <p>{{ ordemDeServico.dataAtribuicao }}</p>
      <p>Tipo do equipamento:</p>
      <p>{{ ordemDeServico.tipoEquipamento }}</p>
      <p>Status:</p>
      <p>{{ ordemDeServico.status }}</p>
      <p>Posição do equipamento:</p>
      <p>{{ ordemDeServico.posicaoEquipamento }}</p>
      <p>Serial do equipamento:</p>
      <p>{{ ordemDeServico.serialEquipamento }}</p>
      <p>Descrição do problema:</p>
      <p>{{ ordemDeServico.descricaoProblema }}</p>
    </div>
  </div>
