import { AutorizacaoService } from './../../services/autorizacao.service';
import { Router, RouterModule } from '@angular/router';
import { Component } from '@angular/core';
import { AuthLoginService } from './../../services/auth-login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  providers: [AuthLoginService]
})
export class LoginComponent {
  loginAutorizado: false | undefined;
  //authService: any;

  /*clicar(){
    alert('Login realizado');
  }*/

  //constructor(private router: Router) {}

  constructor(
    private router: Router,
    private autorizacaoService:AutorizacaoService,
    private authLoginService: AuthLoginService
  ) {}

  funcionarioLogoPath = 'assets/images/ServiSysLogoBranca.png';
  coordenadorLogoPath = 'assets/images/ServiSysLogoADM.png';
  cpdLogoPath = 'assets/images/ServiSysLogoCPD.png';

  redirecionaHome() {
    this.router.navigate(['/principal/home']);
  }

  obterDescricaoLogin = () =>
     this.autorizacaoService.obterLoginStatus() ? "Estou Autorizado" : "Nao Estou Autorizado";

  loginClick(){
    if (this.autorizacaoService.obterLoginStatus())
      this.autorizacaoService.deslogar();
    else 
      this.autorizacaoService.autorizar();
  }

  usuario: string = '';
  senha: string = '';


  fazerLogin(): void {
    if (this.authLoginService.fazerLogin(this.usuario, this.senha)) {
      // Redirecionar para a rota correta após o login
      // Exemplo: Funcionário vai para '/funcionario/home'
      // Coordenador vai para '/coordenador/home-coord'
      // CPD vai para '/cpd/home-cpd'
      switch (this.authLoginService.getUsuarioLogado()) {
        case 'funcionario':
          window.location.href = '/funcionario/home';
          break;
        case 'coordenador':
          window.location.href = '/coordenador/home-coord';
          break;
        case 'cpd':
          window.location.href = '/cpd/home-cpd';
          break;
        default:
          // Redirecionar para a página de login em caso de falha
          window.location.href = '/login';
          break;
      }
    } else {
      alert('Usuário ou senha inválidos');
    }
  }
}
