import { Component, Input } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';

@Component({
  selector: 'app-menu-lateral',
  templateUrl: './menu-lateral.component.html',
  styleUrls: ['./menu-lateral.component.scss']
})
export class MenuLateralComponent {

  menuSelecionado = 'Nova ordem';

  botao1FuncVisivel: boolean = true;
  botao2FuncVisivel: boolean = true;

  botao1CoordVisivel: boolean = false;
  botao2CoordVisivel: boolean = false;

  mostrarBotaoNovaOrdem: boolean = true;
  mostrarBotaoSuasOrdens: boolean = true;
  router: any;

  constructor() {}

  ngOnInit(): void {
    this.router.events.subscribe((event: any) => {
      if (event instanceof NavigationEnd) {
        // Verifica a rota atual e atualiza a visibilidade dos botões
        const rotaAtual = this.router.url;
        if (rotaAtual === '/home' || rotaAtual === '/nova-ordem' || rotaAtual === '/suas-ordens') {
          this.botao1FuncVisivel = true;
          this.botao2FuncVisivel = true;

          this.botao1CoordVisivel = false;
          this.botao2CoordVisivel = false;
        } else if (rotaAtual === '/home-coord'){
          this.botao1FuncVisivel = false;
          this.botao2FuncVisivel = false;

          this.botao1CoordVisivel = true;
          this.botao2CoordVisivel = true;
        }
      }
    });
  }

  botaoClick(botao: string){ //recebe qual botão foi clicado
    this.menuSelecionado === botao;
  }

  isSelected: boolean = false;

  onButtonClicked() {
    this.isSelected = !this.isSelected;
  }

  //esconde os botões quando clicado
  hideBotaoNovaOrdem() {
    this.mostrarBotaoNovaOrdem = false;
  }

  hideBotaoSuasOrdens() {
    this.mostrarBotaoSuasOrdens = false;
  }

  /*botão selecionado*/
  botaoSelecionado: string | null = null;

  selecionarBotao(botao: string): void {
    this.botaoSelecionado = botao;
  }

  desselecionarBotoes(): void {
    this.botaoSelecionado = null;
  }



  // menu.component.ts

  /*@Input() userType: string | undefined;

  onButtonClicked(descricao: string) {
    // Lógica para tratar o clique do botão
    console.log('Botão clicado:', descricao);
  }*/



}
<nav class="menu">
    <div class="image-container flex flex-center">
        <img src="./assets/images/ServiSysLogoBranca.png" alt="logo ServiSys" [routerLink]="'/principal/home'">
    </div> 
    <hr class="linha">
    <div class="menu-container flex opcoes">
        <!--botões menu funcionário-->
        <app-botao-menu *ngIf="botao1FuncVisivel" [selecionado]="botaoSelecionado === 'nova-ordem'" [descricao]="'Nova ordem'"
        (clicked)="onButtonClicked(); selecionarBotao('nova-ordem')" (click)="hideBotaoNovaOrdem()" [routerLink]="'/principal/nova-ordem'">
        </app-botao-menu>
        <app-botao-menu *ngIf="botao2FuncVisivel" [selecionado]="botaoSelecionado === 'suas-ordens'" [descricao]="'Suas ordens'"
            (clicked)="onButtonClicked();  selecionarBotao('suas-ordens')" (click)="hideBotaoSuasOrdens()" [routerLink]="'/principal/suas-ordens'">
        </app-botao-menu>


        <!--botões menu coordenador de ti-->
        <app-botao-menu *ngIf="botao1CoordVisivel" [selecionado]="isSelected" [descricao]="'Aprovações pendentes'"
        (clicked)="onButtonClicked()" (click)="hideBotaoNovaOrdem()" [routerLink]="'/principal/aprovacoes'">   
        </app-botao-menu> 
        <app-botao-menu *ngIf="botao2CoordVisivel" [descricao]="'Suas aprovações'"
        (clicked)="onButtonClicked()" (click)="hideBotaoSuasOrdens()" [routerLink]="'/principal/suas-aprovacoes'">   
        </app-botao-menu>

        <!--botões menu CPD-->
        <app-botao-menu  *ngIf="botao1CoordVisivel" [selecionado]="isSelected" [descricao]="'Ordem de serviço'"
        (clicked)="onButtonClicked()" (click)="hideBotaoNovaOrdem()" [routerLink]="'/principal/os-cpd'">   
        </app-botao-menu> 
        <app-botao-menu  *ngIf="botao2CoordVisivel" [descricao]="'Suas manutenções'"
        (clicked)="onButtonClicked()" (click)="hideBotaoSuasOrdens()" [routerLink]="'/principal/manutencoes'">   
        </app-botao-menu>

        <!--fazendo junção dos dois códigos abaixo-->
        <!--<app-botao-menu 
        [descricao]="'Nova ordem'"
        [selecionado]="menuSelecionado == 'Nova ordem'"
        (click)="botaoClick('Nova ordem')"
        >         
        </app-botao-menu>
        <app-botao-menu [descricao]="'Suas ordens'"
        [selecionado]="menuSelecionado == 'Suas ordens'"
        (click)="botaoClick('Suas ordens')"
        >   
        </app-botao-menu> --> 
    </div>

    <!--<div class="menu-container flex opcoes">
        <ng-template [appBotaoMenuDirective]="userType">
            <app-botao-menu [descricao]="'Nova ordem'" (clicked)="onButtonClicked('novaOrdem')"></app-botao-menu>
        </ng-template>
    </div>-->

</nav>
