import { Component, Input } from '@angular/core';


@Component({
  selector: 'app-tabela-ordens',
  templateUrl: './tabela-ordens.component.html',
  styleUrls: ['./tabela-ordens.component.scss']
})
export class TabelaOrdensComponent {
  @Input() ordens: any[] = [
    { id: 'OS4568SA', status: 'Em andamento', data: '14-09-2023' },
    { id: 'OS4567SA', status: 'Concluída', data: '11-07-2023' },
    { id: 'OS4566SA', status: 'Rejeitado', data: '27-06-2023' }
  ];

}
<div class="suas-ordens">
    <table>
        <tr>
            <td>ID da ordem</td>
            <td>Status da ordem</td>
            <td>Data da atribuição</td>
            <td></td>
        </tr>
        <tbody>
            <tr *ngFor="let ordem of ordens">
                <td>{{ ordem.id }}</td>
                <td>{{ ordem.status }}</td>
                <td>{{ ordem.data }}</td>
                <td><button (click)="openModal(ordem)">Ver</button></td>
            </tr>
        </tbody>
    </table>
</div>
