import { Component } from '@angular/core';
import { SwitchService } from 'src/app/services/switch.service';
import { FormsModule } from '@angular/forms';
import { Observable } from 'rxjs';
import { OrdemServico } from 'src/app/models/ordem-servico';
import { OrdemService } from 'src/app/services/ordem.service';
import { User } from 'src/app/models/user';
import { EstadoOrdemServicoEnum } from 'src/app/models/estado-ordem-servico-enum';
import { OrdemEquipamento } from 'src/app/models/ordem-equipamento';
import { Lancamento } from 'src/app/models/lancamento';

@Component({
  selector: 'app-form-nova-os',
  templateUrl: './form-nova-os.component.html',
  styleUrls: ['./form-nova-os.component.scss']
})
export class FormNovaOsComponent {

  modalSwitch: boolean = false;
  //formulario: any = {};

  id: number = 0;
  dataOrdemServico: Date = new Date();
  descricaoProblema: string = '';
  aprovada: boolean = false;
  localEquipamento: string = '';
  tipoEquipamento: string = '';
  serialEquipamento: string = '';
  estadoOrdemServico: EstadoOrdemServicoEnum | undefined;
  userId: number = 0;
  user: User | undefined;
  ordemEquipamentos?: OrdemEquipamento[];
  lancamentos?: Lancamento[];


  constructor(
    private modalSS: SwitchService,
     private ordemService: OrdemService) {

     }

  ngOnInit(){
    // executando observable
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
  }

  openModal(){
    this.modalSwitch = true;
  }

  //ordens  : Ordem[] = []
  ordens = new Observable<OrdemServico[]>();


  /*cadastrarOrdem(){
    if(!this.localEquipamento || !this.tipoEquipamento)
      return;

    this.OrdemServico.cadastrarOrdem({ 
      id: this.id,
      dataOrdemServico: this.dataOrdemServico,
      descricaoProblema: this.descricaoProblema,
      aprovada: this.aprovada,
      localEquipamento: this.localEquipamento,
      tipoEquipamento: this.tipoEquipamento,
      serialEquipamento: this.serialEquipamento,
      estadoOrdemServico: this.estadoOrdemServico,
      userId: this.userId,
      user: this.user })
    .subscribe(_ => this.obterOrdensCadastradas())
  }*/


 // openModel(){
   /* // Verifica se todos os campos obrigatórios foram preenchidos
    if (this.local.trim() === '' && this.tipo.trim() === '' && this.posicao.trim() === '' 
    && this.serial.trim() === '' &&    this.descricao.trim() === ''){
      alert("Por favor, preencha todos os campos."); // Caso contrário, exibe um alerta
    } else {
      this.modalSwitch = true; // Abre o modal se todos os campos estiverem preenchidos
    }*/
  //}


  /*
   modalVisible = false;

   openModal() {
     this.modalVisible = true;
     document.getElementById('myModal')!.style.display = 'block'; // Exibe o modal
   }

   closeModal() {
     this.modalVisible = false;
     document.getElementById('myModal')!.style.display = 'none'; // Oculta o modal
   }

   onSubmit(form: any) {
     if (form.valid) {
       this.openModal();
     }
   }
  */
}
<div class="container">
    <form class="form-flex"> <!--#form="ngForm" (ngSubmit)="onSubmit(form)"-->
        <div class="group-local">
            <label for="local">Local de ocorrência*</label>
            <select type="local" id="local" name="local" [(ngModel)]="localEquipamento" required>
                <option value=""></option>
                <option value="Lab 1">Lab 1</option>
                <option value="Lab 2">Lab 2</option>
                <option value="Lab 3">Lab 3</option>
            </select>
        </div>

        <div class="group-tipo">
            <label for="tipo">Tipo do equipamento*</label>
            <select type="tipo" id="tipo" name="tipo" [(ngModel)]="tipoEquipamento" required>
                <option value=""></option>
                <option value="Lab 1">Lab 1</option>
                <option value="Lab 2">Lab 2</option>
                <option value="Lab 3">Lab 3</option>
            </select>
        </div>

        <div class="group-posicao">
            <label for="posicao">Posição do equipamento* (?)</label>
            <select type="posicao" id="posicao" name="ambiente" [(ngModel)]="ambienteEquipamento" required>
                <option value=""></option>
                <option value="Fileira 1">Fileira 1</option>
                <option value="Fileira 2">Fileira 2</option>
                <option value="Fileira 3">Fileira 3</option>
            </select>
        </div>

        <div class="group-serial">
            <label for="serial">Serial do equipamento*</label>
            <input type="number" type="number" id="serial" name="serial" [(ngModel)]="serialEquipamento" required>
        </div>

        <div class="group-descricao">
            <label for="descricao">Descrição do problema</label>
            <textarea type="descricao" id="descricao" name="descricao" [(ngModel)]="descricao" rows="5" cols="50" required></textarea>
        </div>

        <button type="submit" class="button" (click)="openModal()" (click)="cadastrarOrdem()">Enviar</button>

    </form>
</div>

<div class="" *ngIf="modalSwitch == true">
    <app-modal></app-modal>
</div>

