import { Component } from '@angular/core';
import { OrdemServico } from 'src/app/models/ordem-servico';
import { OrdemService } from 'src/app/services/ordem.service';
import { SwitchService } from 'src/app/services/switch.service';

@Component({
  selector: 'app-os-cpd',
  templateUrl: './os-cpd.component.html',
  styleUrls: ['./os-cpd.component.scss']
})
export class OsCpdComponent {

  modalSwitch: boolean = false;

  public ordens: OrdemServico[] = [];
  public ordensFiltradas: OrdemServico[] = [];

  private _filtrosListado: string = '';

  public get filtroLista(): string{
    return this._filtrosListado;
  }

  public set filtroLista(value: string){
    this._filtrosListado = value;
    this.ordensFiltradas = this.filtroLista ? this.filtrarOrdens(this.filtroLista) : this.ordens;
  }

  // public filtrarOrdens(filtrarPor: string):any{
  //   filtrarPor = filtrarPor.toLocaleLowerCase();
  //   return this.ordens.filter( //o tema é oq vc vai filtrar, mudar pelo certo
  //   ordem.id.toLocaleLowerCase().indexOf(filtrarPor) !== -1 || 
  //   ordem.local.toLocaleLowerCase().indexOf(filtrarPor) !== -1
  //     //(ordem: { tema: string; }) => ordem.tema.toLocaleLowerCase().indexOf(filtrarPor) !== -1 
  //     //||
  //     //ordem.local.toLocaleLowerCase().indexOf(filtrarPor) !== -1
  //   );
  // }

  public filtrarOrdens(filtrarPor: string): OrdemServico[] {
    filtrarPor = filtrarPor.toLocaleLowerCase();
    return this.ordens.filter(
      ordem => ordem.descricaoProblema.toLocaleLowerCase().indexOf(filtrarPor) !== -1 
    );
  }

  constructor(

    private modalSS: SwitchService,
    private ordemService: OrdemService) {
      // console.log('TO AQUI', environment.api)
      //this.obterOrdensCadastradas();
  }

  public ngOnInit(){
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
    this.GetOrdemServico();
  }

  public openModel2(){
    this.modalSwitch = true;
  }

  public GetOrdemServico(): void {
    this.ordemService.GetOrdemServico().subscribe({
      next: (ordens: OrdemServico[]) => {
      this.ordens = ordens;
      this.ordensFiltradas = this.ordens;
      },
      error: (error:any) => console.log(error)
    });
  }

}
<div class="txt-os">
    <p>Nessa página você encontra suas requisições de ordens de serviço</p>
</div>

<button type="button" [routerLink]="'/principal/home-coord'">
    Todas as ordens
  </button>

  <button type="button" [routerLink]="'/principal/registration'">
    Concluídas
  </button>

  <button type="button" [routerLink]="'/principal/aprovacoes'">
    Rejeitadas
  </button>

  <div class="suas-ordens">
    <table>
        <thead>
        <tr>
            <td>ID da ordem</td>
            <td>Status da ordem</td>
            <td>Data da atribuição</td>
            <td></td>
        </tr>
        </thead>
        <tbody *ngIf="ordens && ordens.length">
            <tr *ngFor="let ordem of ordensFiltradas "><!-- ordens$ | async-->

                <td class="id">{{ ordem.id }}</td>

                <td>
                    {{ ordem.estadoOrdemServico }}
                    <div class="bolinha"></div>
                </td>

                <td class="data">{{ ordem.dataOrdemServico }}</td>

                <td><button (click)="openModel2()"> Ver </button></td> <!--ordem.bt-->

            </tr>
        </tbody>
        <tfoot *ngIf="!ordens.length">
            <tr>
                <td colspan="8">
                    <h4>
                        Nenhuma ordem encontrada!
                    </h4>
                </td>
            </tr>
        </tfoot>
    </table>
</div>
