import { Component } from '@angular/core';
import { AbstractControlOptions, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { ValidatorField } from 'src/app/helpers/validator-field';
import { EstadoOrdemServicoEnum } from 'src/app/models/Enum/estado-ordem-servico-enum';
import { OrdemServico } from 'src/app/models/ordem-servico';
import { OrdemService } from 'src/app/services/ordem.service';
import { SwitchService } from 'src/app/services/switch.service';
import { OrdemCompartilhadaService } from 'src/app/services/ordem-compartilhada.service';

@Component({
  selector: 'app-nova-ordem-func',
  templateUrl: './nova-ordem-func.component.html',
  styleUrls: ['./nova-ordem-func.component.scss']
})
export class NovaOrdemFuncComponent {

  condicao1: boolean = true;
  modalSwitch: boolean = false;
  formNO!: FormGroup;
  //ordens = new Observable<OrdemServico[]>();

  ordens = {} as OrdemServico;
  modalMessage: string = '';

  //ordens  : Ordem[] = []
  //get f(): any { return this.form.controls; }

  constructor(
  private modalSS: SwitchService,
  private ordemService: OrdemService,
  private fb: FormBuilder,
  private ordemCompartilhadaService: OrdemCompartilhadaService) {}

  ngOnInit(): void{
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
    this.validation();
  }

  get fn(): any {
    return this.formNO.controls;
  }

  private validation(): void {

    this.formNO = this.fb.group({
      localEquipamento: ['', Validators.required],
      tipoEquipamento: ['', Validators.required],
      serialEquipamento: ['',[Validators.required, Validators.minLength(4)]],
      posicaoEquipamento: ['', Validators.required],
      descricaoProblema: ['', [Validators.required]],
      estadoOrdemServico: [EstadoOrdemServicoEnum.EmAnalise, Validators.required]
    });
  }

  public resetForm(): void {
    this.formNO.reset();
  }

  public cssValidator(campoForm: FormControl): any {
    return {'is-invalid': campoForm.errors && campoForm.touched};
  }

  //salvar ordens com api
  public salvarAlteracao(): void {
    if(this.formNO.valid){

      this.ordens = {... this.formNO.value};
      this.ordemCompartilhadaService.mudarOrdem(this.ordens);
      this.ordemService.PostOrdemServico(this.ordens).subscribe(
        () => {
        this.modalMessage = 'Evento salvo com sucesso!';
        this.openModal();
        },
        (error: any) =>{
          console.error(error);
          this.modalMessage = 'Erro ao salvar evento';
          this.openModal(); //fazer modal de erro
        }


      );

      /*this.ordemService.PutOrdemServico(this.ordens.id, this.ordens).subscribe(
        () => {
        //this.modalMessage = 'Evento salvo com sucesso!';
        this.openModal();
        },
        (error: any) =>{
          console.error(error);
          //this.modalMessage = 'Erro ao salvar evento';
          this.openModal();
        }
      );*/

    }
  }

  openModal(){
    this.modalSwitch = true;
  }

}
<div class="txt-os">
    <p>Preencha abaixo as informações para requisitar a ordem de serviço:</p>
</div>

<div class="container">
    <form class="form-flex" [formGroup]="formNO">
        <div class="group-local">
            <label>Local de ocorrência*</label>
            <input type="text" class="form-control" id="local"
            [ngClass]="cssValidator(fn.localEquipamento)" 
            formControlName="localEquipamento">
            <div *ngIf="fn.localEquipamento.errors?.required && fn.localEquipamento.touched" class="invalid-feedback">
				Local do equipamento é obrigatório.
			</div>
        </div>

        <div class="group-tipo">
            <label>Tipo do equipamento*</label>
            <input type="text" class="form-control" id="tipo" 
            [ngClass]="cssValidator(fn.tipoEquipamento)" 
            formControlName="tipoEquipamento" >
            <div *ngIf="fn.tipoEquipamento.errors?.required && fn.tipoEquipamento.touched" class="invalid-feedback">
				Tipo do equipamento é obrigatório.
			</div>
        </div>

        <div class="group-local">
            <label>Posição do equipamento*</label>
            <input type="text" class="form-control" id="posicao"
            [ngClass]="cssValidator(fn.posicaoEquipamento)" 
            formControlName="posicaoEquipamento">
            <div *ngIf="fn.posicaoEquipamento.errors?.required && fn.posicaoEquipamento.touched" class="invalid-feedback">
				Local do equipamento é obrigatório.
			</div>
        </div>

        <div class="group-serial">
            <label>Serial do equipamento*</label>
            <input class="form-control" type="text" id="serial"
            [ngClass]="cssValidator(fn.serialEquipamento)"
            formControlName="serialEquipamento" >
            <div *ngIf="fn.serialEquipamento.errors?.required && fn.serialEquipamento.touched" class="invalid-feedback">
				Serial é obrigatório.
			</div>
            <div *ngIf="fn.serialEquipamento.errors?.minlength && fn.serialEquipamento.touched" class="invalid-feedback">
				Serial deve conter no mínimo 6 caracteres.
			</div>
        </div>

        <div class="group-descricao">
            <label>Descrição do problema</label>
            <textarea type="text" id="descricao"
            rows="5" cols="50" class="form-control"
            [ngClass]="cssValidator(fn.descricaoProblema)"
            formControlName="descricaoProblema"></textarea>
            <div *ngIf="fn.descricaoProblema.errors?.required && fn.descricaoProblema.touched" class="invalid-feedback">
				Descrição do problema é obrigatória.
			</div>
        </div>

        <button type="submit" class="button" [disabled]="!formNO.valid" (click)="salvarAlteracao()">Enviar</button>
    </form>
</div>

<div class="" *ngIf="modalSwitch == true">
    <app-modal></app-modal>
</div>

