import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthLoginService {
  login(login: any, senha: any) {
    throw new Error('Method not implemented.');
  }

  private usuarioLogado: string | null = null;

  constructor() { }

  fazerLogin(usuario: string, senha: string): boolean {
    // Lógica de autenticação aqui (pode ser uma chamada HTTP para um servidor)
    // Exemplo simplificado
    if (usuario === 'funcionario' && senha === '123') {
      this.usuarioLogado = 'funcionario';
      localStorage.setItem('usuarioLogado', 'funcionario');
      return true;
    } else if (usuario === 'coordenador' && senha === '456') {
      this.usuarioLogado = 'coordenador';
      localStorage.setItem('usuarioLogado', 'coordenador');
      return true;
    } else if (usuario === 'cpd' && senha === '789') {
      this.usuarioLogado = 'cpd';
      localStorage.setItem('usuarioLogado', 'cpd');
      return true;
    }
    return false;
  }

  getUsuarioLogado(): string | null {
    return this.usuarioLogado;
  }

  logout(): void {
    this.usuarioLogado = null;
    localStorage.removeItem('usuarioLogado'); // Remover informações de login do localStorage ao fazer logout
  }
}
