import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { OrdemServico } from '../models/ordem-servico';
import { Observable, catchError, of, take } from 'rxjs';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class OrdemService {

  baseURL = environment.apiURL + 'api/OrdemServico';
  tokenHeader: HttpHeaders | null = null;
  //tokenHeader = new HttpHeaders({ 'Authorization': `Bearer ${JSON.parse(localStorage.getItem('user')).token}`});

  constructor(private http: HttpClient) {
    this.setTokenHeader();
  }

  private setTokenHeader(): void {
    const user = localStorage.getItem('user');
    if (user) {
      try {
        const parsedUser = JSON.parse(user);
        if (parsedUser && parsedUser.tokenHeader) {
          this.tokenHeader = new HttpHeaders({ 'Authorization': `Bearer ${parsedUser.tokenHeader}` });
        } else {
          console.error('Token não encontrado no objeto usuário.');
        }
      } catch (e) {
        console.error('Erro ao analisar o JSON armazenado em localStorage:', e);
      }
    } else {
      console.error('Usuário não encontrado no localStorage.');
    }
  }

  public GetOrdemServicoByUsuario(idUsuario: number): Observable<OrdemServico[]>  {
    return this.http.get<OrdemServico[]>(`${this.baseURL}/usuario/${idUsuario}`).pipe(
      catchError(this.handleError<OrdemServico[]>('GetOrdemServicoByUsuario', []))
    );
  }

  public GetOrdemServicoById(id: number): Observable<OrdemServico> {
    return this.http.get<OrdemServico>(`${this.baseURL}/${id}`).pipe(
      catchError(this.handleError<OrdemServico>('GetOrdemServicoById'))
    );
  }

  public PostOrdemServico(ordens: OrdemServico): Observable<OrdemServico> {
    return this.http.post<OrdemServico>(this.baseURL, ordens).pipe(
      catchError(this.handleError<OrdemServico>('PostOrdemServico'))
    );
  }

  public PutOrdemServico(id: number, ordens: OrdemServico): Observable<OrdemServico> {
    return this.http.put<OrdemServico>(`${this.baseURL}${id}`, ordens).pipe(
      catchError(this.handleError<OrdemServico>('PutOrdemServico'))
    );
  }

  public PatchOrdemServico(id: number, changes: Partial<OrdemServico>): Observable<any> {
    return this.http.patch(`${this.baseURL}/Aprovado/${id}`, changes).pipe(
      catchError(this.handleError<any>('PatchOrdemServico'))
    );
  }

  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {
      console.error(error); // Logar o erro para análise

      // Devolver um resultado seguro para a aplicação continuar funcionando
      return of(result as T);
    };
  }
}
