import { Component } from '@angular/core';
import { AbstractControlOptions, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { ValidatorField } from 'src/app/helpers/validator-field';
import { EstadoOrdemServicoEnum } from 'src/app/models/estado-ordem-servico-enum';
import { Lancamento } from 'src/app/models/lancamento';
import { OrdemEquipamento } from 'src/app/models/ordem-equipamento';
import { OrdemServico } from 'src/app/models/ordem-servico';
import { User } from 'src/app/models/user';
import { OrdemService } from 'src/app/services/ordem.service';
import { SwitchService } from 'src/app/services/switch.service';

@Component({
  selector: 'app-nova-ordem-func',
  templateUrl: './nova-ordem-func.component.html',
  styleUrls: ['./nova-ordem-func.component.scss']
})
export class NovaOrdemFuncComponent {

  condicao1: boolean = true;

  modalSwitch: boolean = false;


  id: number = 0;
  dataOrdemServico: Date = new Date();
  descricaoProblema: string = '';
  aprovada: boolean = false;
  localEquipamento: string = '';
  tipoEquipamento: string = '';
  serialEquipamento: string = '';
  estadoOrdemServico: EstadoOrdemServicoEnum | undefined;
  userId: number = 0;
  user: User | undefined;
  ordemEquipamentos?: OrdemEquipamento[];
  lancamentos?: Lancamento[];

  formNO!: FormGroup;


  constructor(
  private modalSS: SwitchService,
  private ordemService: OrdemService,
  private fb: FormBuilder) {}

  ngOnInit(){
    // executando observable
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
    this.validation();
  }

  openModal(){
    this.modalSwitch = true;
  }

  //ordens  : Ordem[] = []
  ordens = new Observable<OrdemServico[]>();


  cadastrarOrdem(){
   /* if(!this.localEquipamento || !this.tipoEquipamento)
      return;

    this.OrdemServico.cadastrarOrdem({ 
      id: this.id,
      dataOrdemServico: this.dataOrdemServico,
      descricaoProblema: this.descricaoProblema,
      aprovada: this.aprovada,
      localEquipamento: this.localEquipamento,
      tipoEquipamento: this.tipoEquipamento,
      serialEquipamento: this.serialEquipamento,
      estadoOrdemServico: this.estadoOrdemServico,
      userId: this.userId,
      user: this.user })
    .subscribe(_ => this.obterOrdensCadastradas())*/
  }

  obterOrdensCadastradas(){ 
    // Substitua 'this.ordem = response' pela atribuição correta à variável 'ordens$'
    this.ordens = this.ordemService.GetOrdemServico();
  }

  get fn(): any {
      return this.formNO.controls;
  }

  public validation(): void {

    const formOptions: AbstractControlOptions = {
      validators: ValidatorField.MustMatch('senha', 'confirmeSenha')
    };


    this.formNO = this.fb.group({
      localEquipamento: ['', Validators.required],
      tipoEquipamento: ['', Validators.required],
      serialEquipamento: ['',[Validators.required, Validators.minLength(4)]],
      descricaoProblema: ['', [Validators.required]],
    }, formOptions);
  }


}
<div class="txt-os">
    <p>Preencha abaixo as informações para requisitar a ordem de serviço:</p>
</div>

<div class="container">
    <form class="form-flex" [formGroup]="formNO">
        <div class="group-local">
            <label>Local de ocorrência*</label>
            <input type="text" class="form-control" id="local"
            [ngClass]="{'is-invalid': fn.localEquipamento.errors && fn.localEquipamento.touched}" 
            formControlName="localEquipamento">
            <div *ngIf="fn.localEquipamento.errors?.required && fn.localEquipamento.touched" class="invalid-feedback">
				O local do equipamento é obrigatório.
			</div>
        </div>

        <div class="group-tipo">
            <label>Tipo do equipamento*</label>
            <input type="text" class="form-control" id="tipo" 
            [ngClass]="{'is-invalid': fn.tipo.errors && fn.tipo.touched}" 
            formControlName="tipo" >
            <div *ngIf="fn.tipo.errors?.required && fn.tipo.touched" class="invalid-feedback">
				Tipo do equipamento é obrigatório.
			</div>
        </div>

        <div class="group-serial">
            <label>Serial do equipamento*</label>
            <input class="form-control" type="text" id="serial"
            [ngClass]="{'is-invalid': fn.serialEquipamento.errors && fn.serialEquipamento.touched}"
            formControlName="serial" >
            <div *ngIf="fn.serialEquipamento.errors?.required && fn.serialEquipamento.touched" class="invalid-feedback">
				Serial é obrigatório.
			</div>
        </div>

        <div class="group-descricao">
            <label>Descrição do problema</label>
            <textarea type="text" id="descricao"
            rows="5" cols="50" class="form-control"
            formControlName="descricao" 
			[ngClass]="{'is-invalid': fn.descricao.errors && fn.descricao.touched}"></textarea>
            <div *ngIf="fn.descricao.errors?.required && fn.descricao.touched" class="invalid-feedback">
				Descrição do problema é obrigatória.
			</div>
        </div>

        <button type="submit" class="button">Enviar</button>
    </form>
</div>

<div class="" *ngIf="modalSwitch == true">
    <app-modal></app-modal>
</div>

