import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

}
<div class="base-flex">
    <img class="logo" src="assets/images/ServiSysLogoBranca.png" alt="logo ServiSys">
    <img class="img-decor" src="assets/images/img-login.png" alt="logo ServiSys">
</div>
<div class="form-flex">
    <h4>Seja bem-vindo(a) ao ServiSys!</h4>
    <p>Entre com suas credenciais para acessar o sistema</p>
    <img class="logo" src="assets/images/ServiSysLogoBranca.png" alt="logo ServiSys">
    <img class="img-decor" src="assets/images/img-login.png" alt="logo ServiSys">
</div>
