import { Component } from '@angular/core';

@Component({
  selector: 'app-modal-os',
  templateUrl: './modal-os.component.html',
  styleUrls: ['./modal-os.component.scss']
})
export class ModalOsComponent {

  // Variável para armazenar os dados da ordem de serviço
  ordemDeServico = {
    numero: 'OS4568SA',
    local: 'Laboratório de informática 07',
    dataAtribuicao: '14-09-2023',
    tipoEquipamento: 'Computador',
    status: 'Em andamento',
    posicaoEquipamento: 'Mesa 04',
    serialEquipamento: '-',
    descricaoProblema: 'O computador não está ligando e ouve bips'
  };

   // Variável para controlar a exibição do modal
   modalAberto = false;

   // Método para abrir o modal
   openModal() {
     this.modalAberto = true;
   }

   // Método para fechar o modal
   closeModal() {
     this.modalAberto = false;
   }

}
