import { Component, Input } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';

@Component({
  selector: 'app-menu-lateral',
  templateUrl: './menu-lateral.component.html',
  styleUrls: ['./menu-lateral.component.scss']
})
export class MenuLateralComponent {

  menuSelecionado = 'Nova ordem';

  botao1FuncVisivel: boolean = true;
  botao2FuncVisivel: boolean = true;

  botao1CoordVisivel: boolean = false;
  botao2CoordVisivel: boolean = false;

  botao1CPDVisivel: boolean = false;
  botao2CPDVisivel: boolean = false;

  mostrarBotaoNovaOrdem: boolean = true;
  mostrarBotaoSuasOrdens: boolean = true;

  rotaAtual: string = ''; // Variável para armazenar a rota atual

  constructor(private router: Router) {}

  ngOnInit(): void {

    //ouvindo eventos de navegação (NavigationEnd) quando a rota é alterada
    this.router.events.subscribe((event: any) => {
      if (event instanceof NavigationEnd) {
        // Atualiza a rota atual com base na rota atual do aplicativo
        this.rotaAtual = event.urlAfterRedirects;

        // Verifica a rota atual e atualiza a visibilidade dos botões
        if (this.rotaAtual == '/home' || this.rotaAtual == '/nova-ordem' || this.rotaAtual == '/suas-ordens') {
          this.botao1FuncVisivel = true;
          this.botao2FuncVisivel = true;

          this.botao1CoordVisivel = false;
          this.botao2CoordVisivel = false;

          this.botao1CPDVisivel = false;
          this.botao2CPDVisivel = false;

        } else if (this.rotaAtual === '/home-coord' || this.rotaAtual === '/aprovacoes' || this.rotaAtual === '/suas-aprovacoes') {
          this.botao1FuncVisivel = false;
          this.botao2FuncVisivel = false;

          this.botao1CoordVisivel = true;
          this.botao2CoordVisivel = true;

          this.botao1CPDVisivel = false;
          this.botao2CPDVisivel = false;

        } if (this.rotaAtual === '/os-cpd' || this.rotaAtual === '/manutencoes') {
          this.botao1FuncVisivel = false;
          this.botao2FuncVisivel = false;

          this.botao1CoordVisivel = false;
          this.botao2CoordVisivel = false;

          this.botao1CPDVisivel = true;
          this.botao2CPDVisivel = true;
        }
      }
    });
  }

  botaoClick(botao: string){ //recebe qual botão foi clicado
    this.menuSelecionado = botao;
  }

  isSelected: boolean = false;

  onButtonClicked() {
    this.isSelected = !this.isSelected;
  }

  //esconde os botões quando clicado
  hideBotaoNovaOrdem() {
    this.mostrarBotaoNovaOrdem = false;
  }

  hideBotaoSuasOrdens() {
    this.mostrarBotaoSuasOrdens = false;
  }

  /*botão selecionado*/
  botaoSelecionado: string | null = null;

  selecionarBotao(botao: string): void {
    this.botaoSelecionado = botao;
  }

  desselecionarBotoes(): void {
    this.botaoSelecionado = null;
  }



}
<nav class="menu">
    <div class="image-container flex flex-center">
        <img src="./assets/images/ServiSysLogoBranca.png" alt="logo ServiSys" [routerLink]="'/principal/home'">
    </div> 
    <hr class="linha">
    <div class="menu-container flex opcoes">
         <!-- Botões para funcionário -->

         <app-botao-menu *ngIf="botao1FuncVisivel"
         [selecionado]="botaoSelecionado === 'nova-ordem'"
         [iconClass]="'fa fa-plus-circle'"
         [descricao]="'Nova ordem'"
         (clicked)="onButtonClicked();selecionarBotao('nova-ordem')"
         [routerLink]="'/principal/nova-ordem'">
         </app-botao-menu>

        <app-botao-menu *ngIf="botao2FuncVisivel"
        [selecionado]="botaoSelecionado === 'suas-ordens'"
        [iconClass]="'fa fa-list'"
        [descricao]="'    Suas ordens '"
        (clicked)="onButtonClicked(); selecionarBotao('suas-ordens')"
        [routerLink]="'/principal/suas-ordens'">
        </app-botao-menu>

        <!-- Botões para coordenador de TI -->
        <app-botao-menu *ngIf="botao1CoordVisivel"
        [selecionado]="botaoSelecionado === 'aprovacoes'"
        [iconClass]="'fa fa-check'"
        [descricao]="'    Aprovações pendentes   '"
        (clicked)="onButtonClicked(); selecionarBotao('aprovacoes')"
        [routerLink]="'/principal/aprovacoes'">
        </app-botao-menu>

        <app-botao-menu *ngIf="botao2CoordVisivel"
        [selecionado]="botaoSelecionado === 'suas-aprovacoes'"
        [iconClass]="'fa fa-list'"
        [descricao]="'    Suas aprovações'"
        (clicked)="onButtonClicked(); selecionarBotao('suas-aprovacoes')"
        [routerLink]="'/principal/suas-aprovacoes'">
        </app-botao-menu>

        <!-- Botões para CPD -->
        <app-botao-menu *ngIf="botao1CPDVisivel"
        [selecionado]="botaoSelecionado === 'os-cpd'"
        [iconClass]="'fa fa-check'"
        [descricao]="'    Ordem de serviço'"
        (clicked)="onButtonClicked(); selecionarBotao('os-cpd')"
        [routerLink]="'/principal/os-cpd'">
        </app-botao-menu>

        <app-botao-menu *ngIf="botao2CPDVisivel"
        [selecionado]="botaoSelecionado === 'manutencoes'"
        [iconClass]="'fa fa-wrench '"
        [descricao]="'    Suas manutenções'"
        (clicked)="onButtonClicked(); selecionarBotao('manutencoes')"
        [routerLink]="'/principal/manutencoes'">
        </app-botao-menu>

        <!--fazendo junção dos dois códigos abaixo-->
