        [descricao]="'Nova ordem'"
        [selecionado]="menuSelecionado == 'Nova ordem'"
        (click)="botaoClick('Nova ordem')"
        >         
        </app-botao-menu>
        <app-botao-menu [descricao]="'Suas ordens'"
        [selecionado]="menuSelecionado == 'Suas ordens'"
        (click)="botaoClick('Suas ordens')"
        >   
        </app-botao-menu> --> 
    </div>

    <!--<div class="menu-container flex opcoes">
        <ng-template [appBotaoMenuDirective]="userType">
            <app-botao-menu [descricao]="'Nova ordem'" (clicked)="onButtonClicked('novaOrdem')"></app-botao-menu>
        </ng-template>
    </div>-->

</nav>
