import { Injectable } from '@angular/core';
import { HttpClient } from "@angular/common/http"
import { environment } from 'src/environments/environment';
import { Ordem } from '../models/ordem';
import { User } from '../models/user';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  private getOrdem = `${environment.api}/api/OrdemServico`;
  private postOrdem = `${environment.api}/api/OrdemServico`;
  private getIdOrdem = `${environment.api}/api/OrdemServico/{id}`;
  private putOrdem = `${environment.api}/api/OrdemServico/{id}`;
  private getUserOrdem = `${environment.api}/api/OrdemServico/usuario/{idUsuario}`;
  private patchIdOrdem = `${environment.api}/api/OrdemServico/Aprovado/{id}`;

  constructor(private httpClient: HttpClient) {
  }

  obterOrdem(){
    return this.httpClient.get<Ordem[]>(this.getOrdem);
  }

  cadastrarOrdem(ordens: Ordem){
    return this.httpClient.post<User>(this.postOrdem,ordens);
  }

  obterIdOrdem(){
    return this.httpClient.get<Ordem[]>(this.getIdOrdem);
  }

  atualizarOrdem(ordens: Ordem){
    return this.httpClient.put<Ordem>(this.putOrdem,ordens);
  }

  obterUserOrdem(){
    return this.httpClient.get<Ordem[]>(this.getIdOrdem);
  }

  //patch
  alteraOrdem(ordens: Ordem){
   // const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    return this.httpClient.patch<Ordem[]>(this.getIdOrdem, ordens);
  }

}
