import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AutorizacaoService {

  autorizado = false;

  constructor() { }

  autorizar(){
    localStorage.setItem("login","sim");
  }

  deslogar(){
    localStorage.clear();
  }

  //se n existir nada retorna falso, se existir retorna true
  obterLoginStatus = ()  => !!localStorage.getItem("login"); 

}
