import { Component, Input, Output, EventEmitter } from '@angular/core';
import { SwitchService } from 'src/app/services/switch.service';

@Component({
  selector: 'app-tabela-ordens',
  templateUrl: './tabela-ordens.component.html',
  styleUrls: ['./tabela-ordens.component.scss']
})
export class TabelaOrdensComponent {
  @Input() ordens: any[] = [
    { id: 'OS4568SA', status: 'Em andamento', data: '14-09-2023', bt: 'ver' },
    { id: 'OS4567SA', status: 'Concluída', data: '11-07-2023', bt: 'ver' },
    { id: 'OS4566SA', status: 'Rejeitado', data: '27-06-2023', bt: 'ver' }
  ];

  @Output() verDetalhes = new EventEmitter<any>();

  openModal(ordem: any) {
    this.verDetalhes.emit(ordem);
  }

  modalSwitch: boolean = false;

  constructor(private modalSS: SwitchService) {

  }

  ngOnInit(){
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
  }

  openModel(){
    this.modalSwitch = true;
  }
}
<div class="suas-ordens">
    <table>
        <tr>
            <td>ID da ordem</td>
            <td>Status da ordem</td>
            <td>Data da atribuição</td>
            <td></td>
        </tr>
        <tbody>
            <tr *ngFor="let ordem of ordens">
                <td>{{ ordem.id }}</td>
                <td>{{ ordem.status }}</td>
                <td>{{ ordem.data }}</td>
                <td><button *ngIf="condicao1" (click)="openModel()">{{ ordem.bt }}</button></td>
            </tr>
        </tbody>
    </table>

    <!--ver como fazer pra diferenciar o modal normal do modal de OS-->
    <div class="" *ngIf="modalSwitch">
        <app-modal></app-modal>
    </div>

</div>
