import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-modal-os',
  templateUrl: './modal-os.component.html',
  styleUrls: ['./modal-os.component.scss']
})
export class ModalOsComponent {

  // Variável para armazenar os dados da ordem de serviço
  ordemDeServico: any;

  // Variável para controlar a exibição do modal
  modalAberto = true;

  constructor() { }

  // Método para abrir o modal e receber a ordem de serviço
  openModal(ordem: any) {
    this.ordemDeServico = ordem;
    this.modalAberto = true;
  }

  // Método para fechar o modal
  closeModal() {
    this.modalAberto = false;
  }
}
