import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from "@angular/common/http";
import { OrdemServico } from '../models/ordem-servico';
import { Observable, take } from 'rxjs';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class OrdemService {

  baseURL = environment.apiURL + 'api/OrdemServico';
  tokenHeader = new HttpHeaders({ 'Authorization': `Bearer ${JSON.parse(localStorage.getItem('user')).token}`});

  constructor(private http: HttpClient) {}

  public GetOrdemServico(): Observable<OrdemServico[]>{
    return this.http
    .get<OrdemServico[]>(this.baseURL, { headers: this.tokenHeader }).pipe(take(1));
    //return this.http.get<OrdemServico[]>(this.baseURL);
  }

  public GetOrdemServicoByUsuario(idUsuario: number): Observable<OrdemServico[]>  {
    return this.http
    .get<OrdemServico[]>(`${this.baseURL}/usuario/${idUsuario}`, { headers: this.tokenHeader }).pipe(take(1));
  }

  public GetOrdemServicoById(id: number): Observable<OrdemServico>{
    return this.http
    .get<OrdemServico>(`${this.baseURL}/${id}`, { headers: this.tokenHeader }).pipe(take(1));
  }

  public PostOrdemServico(ordens: OrdemServico): Observable<OrdemServico>{
    return this.http
    .post<OrdemServico>(this.baseURL, ordens, { headers: this.tokenHeader }).pipe(take(1));
  }

  /*public PutOrdemServico(id: number, ordens: OrdemServico): Observable<OrdemServico>{
    return this.http
    .put<OrdemServico>(`${this.baseURL}${id}`,ordens, { headers: this.tokenHeader }).pipe(take(1));
  }*/

  public PatchOrdemServico(id: number, changes: Partial<OrdemServico>): Observable<any> {
    return this.http
    .patch(`${this.baseURL}/Aprovado/${id}`, changes, { headers: this.tokenHeader }).pipe(take(1));
  }

}
