import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  tipoUsuario: string | undefined;
  logoPath: string | undefined;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.data.subscribe((data) => {
      this.tipoUsuario = data.tipoUsuario;
      this.carregarLogo();
    });
  }

  carregarLogo(): void {
    switch (this.tipoUsuario) {
      case 'funcionario':
        this.logoPath = 'caminho-do-logotipo-funcionario';
        break;
      case 'coordenador':
        this.logoPath = 'caminho-do-logotipo-coordenador';
        break;
      case 'cpd':
        this.logoPath = 'caminho-do-logotipo-cpd';
        break;
      default:
        this.logoPath = 'caminho-do-logotipo-padrao';
        break;
    }
  }
}
