import { Component, Input, Output, EventEmitter } from '@angular/core';
import { catchError } from 'rxjs';
import { OrdemService } from 'src/app/services/ordem.service';
import { SwitchService } from 'src/app/services/switch.service';
import { environment } from 'src/environments/environment';


@Component({
  selector: 'app-tabela-ordens',
  templateUrl: './tabela-ordens.component.html',
  styleUrls: ['./tabela-ordens.component.scss']
})
export class TabelaOrdensComponent {

  modalSwitch: boolean = false;
  public ordens: any = [];

  constructor(
    private modalSS: SwitchService,
    private ordemService: OrdemService) {
      // console.log('TO AQUI', environment.api)
      //this.obterOrdensCadastradas();
  }

  ngOnInit(){
    this.modalSS.$modal.subscribe((valor) => {this.modalSwitch = valor}); 
    this.obterOrdensCadastradas();
  }

  openModel2(){
    this.modalSwitch = true;
  }

  public obterOrdensCadastradas(): void {
    /*this.http.get('http://localhost:5124/api/OrdemServico').subscribe(
      response => this.ordens = <any[]>response,
      error => console.log(error)
    );*/

    this.http.get<any[]>('http://localhost:5124/api/OrdemServico')
    .pipe(
      catchError(error => {
        console.error('Erro na requisição:', error);
        throw 'Erro ao obter as ordens cadastradas. Por favor, tente novamente mais tarde.';
      })
    )
    .subscribe({
      next: (response: any[]) => {
        this.ordens = response;
        console.log('Ordens cadastradas:', this.ordens);
      },
      error: (error: any) => {
        console.error('Erro na requisição:', error);
        // Trate o erro aqui conforme necessário
      }
    });

    // this.apiService.obterOrdem().
    // subscribe(ordens => this.ordens = ordens)
    //this.ordens = this.ordemService.obterOrdem();
  }

}
<div class="suas-ordens">
    <table>
        <thead>
        <tr>
            <td>ID da ordem</td>
            <td>Status da ordem</td>
            <td>Data da atribuição</td>
            <td></td>
        </tr>
        </thead>
        <tbody *ngIf="ordens && ordens.length">
            <tr *ngFor="let ordem of ordens "><!-- ordens$ | async-->

                <td class="id">{{ ordem.id }}</td>

                <td [ngClass]="{ 
                    'green-background': ordem.estadoOrdemServico === 'Concluída',
                    'yellow-background': ordem.estadoOrdemServico === 'Em andamento',
                    'red-background': ordem.estadoOrdemServico === 'Rejeitado' }">
                  {{ ordem.estadoOrdemServico }}
                  <div class="bolinha"></div>
                </td>

                <td class="data">{{ ordem.dataOrdemServico }}</td>

                <td><button  (click)="openModel2()">{{ ordem.bt }}</button></td>

            </tr>
        </tbody>
        <tfoot *ngIf="!ordens.length">
            <tr>
                <td colspan="8">
                    <h4>
                        Nenhuma ordem encontrada!
                    </h4>
                </td>
            </tr>
        </tfoot>
    </table>

    <!--{{ordens | json}}-->

    <div class="" *ngIf="modalSwitch">
        <app-modal-os></app-modal-os>
    </div>

</div>
